css:
table {
    
    margin: 0 auto; /* or margin: 0 auto 0 auto */
    width: 90%;
    line-height: 1;
     border:1px;
  }
  
  #sales td, #sales th {
  border: 1px solid #21130d;
  padding: 8px;
}
  
 #sales {
  font-family: Arial, Helvetica, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

#sales th {
  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: #DCDCDC;
  color: #21130d;
  font-weight: bold;
}

#main {
   margin-left: 20px;
   margin-right: 20px;
}
@media all {
   div .printedby {
       position: fixed;
       bottom: 0;
       text-align: center;
       font-size: xx-small;
       width: 100% !important;
   }
}
