css:
/* Make the table responsive */
.table {
    width: 90%;
    border-collapse: collapse;
    margin: 0 auto;
}

.table th, .table td {
    padding: 8px;
    border: 1px solid #ddd;
    text-align: center;
    word-wrap: break-word;
}

/* Dark gray header with black, bold text */
.table thead th {
    background-color: #d3d3d3; /* Light gray background */
    color: black; /* Black text */
    font-weight: bold; /* Bold text */
}

/* Responsive design for smaller screens */
@media screen and (max-width: 768px) {
    .table, .table thead, .table tbody, .table th, .table td, .table tr {
        display: block;
        width: 90%;
    }

    .table thead {
        display: none; /* Hide the table header */
    }

    .table tr {
        margin-bottom: 15px;
        border: 1px solid #ddd;
        padding: 10px;
    }

    .table td {
        text-align: right;
        padding-left: 50%;
        position: relative;
    }

    .table td::before {
        content: attr(data-label);
        position: absolute;
        left: 10px;
        font-weight: bold;
        text-align: left;
    }
}

/* Style for printed documents */
@media print {
    .table {
        font-size: small;
    }

    .table thead th {
        background-color: #d3d3d3 !important; /* Light gray */
        color: black !important; /* Black text */
        font-weight: bold !important;
    }

    .printedby {
        position: fixed;
        bottom: 0;
        text-align: center;
        font-size: xx-small;
        width: 100% !important;
    }
}
